import { Button, EmptyState, Form, FormLayout, Layout, Page, TextField } from '@shopify/polaris';
import Cookies from 'js-cookie';
import { ResourcePicker } from '@shopify/app-bridge-react';
import store from 'store-js';
import ResourceListWithProducts from '../components/ResourceList';
import CreateTestVariant from '../components/CreateTestVariant';

const img = 'https://cdn.shopify.com/s/files/1/0757/9955/files/empty-state.svg';

class Index extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      resourcePickerOpen: false,
      variantSelected: false,
    };
    this.handleProductSubmit.bind(this)
    this.childHandler = this.childHandler.bind(this);
  }

  childHandler(variantSelectedFromChild) {
    this.setState({
      variantSelected: variantSelectedFromChild
    });
  }

  handleProductSubmit = (data, shopID) => {
    //data param is retrieved from gql, "old data", new data is stored in this.state
    //send all relevant fields form old and new data with request
    var product = new Product
    (data.nodes[0].id, shopID, data.nodes[0].title,
        data.nodes[0].images.edges[0].node.originalSrc, data.nodes[0].description, 0, 0, 0, this.state.productTitle,
        this.state.productImage, this.state.productDescription, 0, 0, 0);
    $.ajax({
      type: "POST",
      url: "./create-product-test",
      data: JSON.stringify(product),
      success: success,
      dataType: 'json'
    });
    const success = () => {
      console.log("Product test submission successful");
    };
  };

  render() {
    const emptyState = !store.get('ids');
    return (
      <Page
        primaryAction={{
          content: 'Select product to test',
          onAction: () => this.setState({ resourcePickerOpen: true }),
        }}
      >
        <ResourcePicker
          resourceType="Product"
          showVariants={false}
          allowMultiple={false}
          open={this.state.resourcePickerOpen}
          onSelection={(resources) => this.handleSelection(resources)}
          onCancel={() => this.setState({ resourcePickerOpen: false })}
        />
        {emptyState ? (
          <Layout>
            <EmptyState
              heading="Test product variants"
              action={{
                content: 'Select products',
                onAction: () => this.setState({ resourcePickerOpen: true }),
              }}
              image={img}
            >
              <p>Select a product to create a test variant.</p>
            </EmptyState>
          </Layout>
        ) :(!this.state.variantSelected ? (
            <ResourceListWithProducts />
          ) : <CreateTestVariant variantCreated={this.childHandler}/>)}
      </Page>
    );
  }

  handleSelection = (resources) => {
    const idsFromResources = resources.selection.map((product) => product.id);
    this.setState({ resourcePickerOpen: false, variantSelected: true});
    store.set('ids', idsFromResources);
  };
}

export default Index;
