import gql from 'graphql-tag';
import { Button, Checkbox, EmptyState, Form, FormLayout, Layout, Page, TextField } from '@shopify/polaris';
import {Context} from "@shopify/app-bridge-react";
import {Redirect} from "@shopify/app-bridge/actions";
import store from "store-js";
import {Query} from "react-apollo";

const GET_PRODUCT_BY_ID = gql`
  query getProducts($ids: [ID!]!) {
    nodes(ids: $ids) {
      ... on Product {
        title
        handle
        descriptionHtml
        id
        images(first: 1) {
          edges {
            node {
              originalSrc
              altText
            }
          }
        }
        variants(first: 1) {
          edges {
            node {
              price
              id
            }
          }
        }
      }
    }
  }
`;

class CreateTestVariant extends React.Component {
    static contextType = Context;
    state = {
        newsletter: false,
        email: '',
    };

    render() {
        const {newsletter, email} = this.state;
        const app = this.context;

        return (
            <Query query={GET_PRODUCT_BY_ID} variables={{ ids: store.get('ids') }}>
                {({ data, loading, error }) => {
                    if (loading) { return <div>Loadingâ€¦</div>; }
                    if (error) { return <div>{error.message}</div>; }
                    console.log(data);
                    return(
            <Form onSubmit={this.redirect}>
                <FormLayout>

                    <TextField
                        value={data.nodes.title}
                        onChange={this.handleChange('email')}
                        label="Product Name"
                        type="text"
                    />

                    <Button submit>Submit</Button>
                </FormLayout>
            </Form>
                    )
                }}
                    </Query>
        );
    }

    redirect = () => {
        this.props.variantCreated(false)
    };

    itemToBeConsumed = () => {
        const item = store.get('item');
        const price = item.variants.edges[0].node.price;
        const variantId = item.variants.edges[0].node.id;
        const discounter = price * 0.1;
        this.setState({ price, variantId });
        return (price - discounter).toFixed(2);
    };

    handleChange = (field) => {
        return (value) => this.setState({[field]: value});
    };
}

export default CreateTestVariant;
