import { Button, Checkbox, EmptyState, Form, FormLayout, Layout, Page, TextField } from '@shopify/polaris';

class FormExample extends React.Component {
    state = {
        newsletter: false,
        email: '',
    };

    render() {
        const {newsletter, email} = this.state;

        return (
            <Form onSubmit={this.handleSubmit}>
                <FormLayout>
                    <Checkbox
                        label="Sign up for the Polaris newsletter"
                        checked={newsletter}
                        onChange={this.handleChange('newsletter')}
                    />

                    <TextField
                        value={email}
                        onChange={this.handleChange('email')}
                        label="Email"
                        type="email"
                        helpText={
                            <span>
                Weâ€™ll use this email address to inform you on future changes to
                Polaris.
              </span>
                        }
                    />

                    <Button submit>Submit</Button>
                </FormLayout>
            </Form>
        );
    }

    handleSubmit = (event) => {
        this.setState({newsletter: false, email: ''});
    };

    handleChange = (field) => {
        return (value) => this.setState({[field]: value});
    };
}

export default FormExample;
